import React, { Component, PropTypes } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: 'ishm@nm.ry',
      firstrname: 'Zhenia',
      lastname: '',
      isValid: false
    };

    // this.handleChange = this.handleChange.bind(this);
  }


  // handleChange(e) {
  //   this.props.onChange(e.target.value)
  // }

  onBtnClickHandler = (e) => {
    e.preventDefault()
    const { email, firstname } = this.state

    this.props.onAddNews({email, firstrname})
  }


  render() {

    const {format} = this.props;
    let content = null;

    const { email, firstname } = this.state

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
      <div>
        <button onClick={this.onBtnClickHandler} type='button'>show</button>
        <input type='text'
        className={styles.feedback__input}
        name='email'
        {...this.props}></input>
      </div>
    }

    return(
      content
    ) 
  }
}

Input.propTypes = {
  onAddNews: PropTypes.func.isRequired, 
}
