import React, { Component } from 'react';

const withInput = (Wrapper) => {
  return class extends Component {
    constructor(props) {
      super(props);
      this.state = {
        noError: false
      }

    }

    render() {
      return (
        <Wrapper {...this.props} />
      )
    }
  }
}

export default withInput;
