import React, {Component} from 'react';
import { Container, Row, Col } from 'reactstrap';
import Title from '../UI/Title/Title';
import Input from '../UI/Input/Input';
import styles from './Feedback.module.scss';


export default class Feedback extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: 'jhjhv',
      firstrname: '',
      lastname: '',
      isValid: true
    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = (event) => {
    let name = event.target.name
    let value = event.target.value
    this.setState({[name]: value})

  }

  validate = () => {
    this.setState((state) => {
      return {
        isValid: !state.isValid
      }
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const isValid = this.state.isValid;
    if (isValid) {
      console.log(this.state)
    }
  }

  render() {


    return (
      <div className={styles.feedback}>
        <Container>
          <Title>
            <h2 className={styles.feedback__name}>Связаться</h2>
          </Title>
          <form onSubmit={this.handleSubmit} action='get'>
            <Row>
              <Col sm={12} md={6}>
                <Input value={this.props.email} onChange={this.handleChange} format={'text'} name='firstrname' placeholder='Имя'/>
              </Col>
              <Col sm={12} md={6}>
                <Input value={this.props.email} onChange={this.handleChange} format={'text'} name='lastname' placeholder='Фамилия'/>
              </Col>
              <Col sm={12} md={6}>
                <Input value={this.state.email} onChange={this.handleChange} format={'email'} placeholder='Электронная почта'/>
              </Col>
              <Col sm={12} md={6}>
                <input format='text' className={`${styles.feedback__input} ${styles.feedback__input_agenda}`} placeholder='Тема сообщения'></input>
              </Col>
              <Col lg={12}>
                <textarea name='feedback-text' id='feedback' cols='30' rows='5' className={styles.feedback__textarea} placeholder='Текст сообщения'></textarea>
              </Col>
              <Col lg={12}>
                <input className={styles.feedback__button} type='submit' value='Отправить'></input>
              </Col>
            </Row>
          </form>
        </Container>
      </div>
    )

  }
}
