import React, { Component } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      noError: true,
      result: false
    };

    this.onBtn = this.onBtn.bind(this)

  }

  onBtn = () => {
    // e.preventDefault();
    const noError = this.state.noError;

    if (this.props.value.length > 2) {
      this.props.validate(noError)
    } else {
      this.props.validate()
    }    
  }


  oneMoreFunc = (e) => {
    e.preventDefault();
    const noError = this.state.noError;

    if (this.props.value.length > 2) {
      this.props.morefunc(noError)
    } else {
      this.props.morefunc()
    }    
  }






  render() {

    const {format} = this.props;
    let content = null;


    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
      <div>
        <button onClick={this.onBtn} type='button'>show</button>
        <input type='text'
        className={styles.feedback__input}
        name='email'
        {...this.props}></input>
      </div>
    }

    return(
      content
    ) 
  }
}
