import React from 'react';
import { Row, Col } from 'reactstrap';
import styles from './Options.module.scss';
import Level from '../Level/Level';
import Button from '../UI/Button/Button';
// import news from '../News/news-data';

const levels =  [
  {id: 1, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 2, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 3, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 4, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 5, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 6, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'}
]


const Options = ({match: {params}}) => {

  const visibleLevels = levels.filter(item => {
    return(item.type === params.filter ? !item.type === 'all' : item)
  })

  function showLevels() {
    const btn = document.querySelector('.options__btn');
    btn.addEventListener('click', () => {
      console.log('done well')
    })
  }

  return (
    <div className={styles.options}>
      <ul className={styles.options__list}>
        <Row>
          {visibleLevels.map((item) => (
            <Col md={6} lg={5} xl={4}>
              <li className={styles.options__item}>
                <Level description={item.description}
                  number={item.id}>
                  {item.id}
                </Level>
              </li>
            </Col>
              ))}                
        </Row>
      </ul>
      <div className={styles.options__button}>
        <Button className={styles.options__btn} onclick={showLevels()} domType={'button'}>Еще уровни</Button>
      </div>
    </div>
  )

}

export default Options;
