import React, {Component} from 'react';
import { Row, Col } from 'reactstrap';
import styles from './Options.module.scss';
import Level from '../Level/Level';
import Button from '../UI/Button/Button';
import news from '../News/news-data';


export default class Options extends Component {
  constructor(props) {
    super(props);
    this.state = {
      levels: news.levels,
      initStatus: false
    }
  }

  btnClicked = () => {
    this.setState((state) => {
      return {
        initStatus: !state.initStatus
      }
    })
  }

  render() {

    let initStatus = this.state.initStatus;

    const visibleLevels = this.state.levels.filter(item => {
      return(item.type === this.props.match.params.filter ? !item.type === 'all' : item)
    })

    const selectedLevels = visibleLevels.map((item) => {
      return (
        <Col md={6} lg={5} xl={4}>
          <li className={styles.options__item}>
            <Level description={item.description}
              number={item.id}>
              {item.id}
            </Level>
          </li>
        </Col>
      )
    })

    const shownLevels = selectedLevels.slice(0, 3);
    const hiddenLevels = selectedLevels.slice(3)
    let addLevels = null;

    if (initStatus) {
      addLevels = hiddenLevels;
    } else {
      addLevels = null
    };


    return (
      <div className={styles.options}>
        <ul className={styles.options__list}>
          <Row>
            {shownLevels}
            {addLevels}
            {/* {visibleLevels.map((item) => (
              <Col md={6} lg={5} xl={4}>
                <li className={styles.options__item}>
                  <Level description={item.description}
                    number={item.id}>
                    {item.id}
                  </Level>
                </li>
              </Col>
                ))}                 */}
          </Row>
        </ul>
        <div onClick={this.btnClicked} className={styles.options__button}>
          <Button domType={'button'}>Еще уровни</Button>
        </div>
      </div>
    ) 

  }

}
