import React from 'react';
import styles from './Filters.module.scss';
import Title from '../Title/Title';
import {NavLink} from 'react-router-dom';
import {Router, Route, Switch} from 'react-router-dom';
import Options from '../../Options/Options';
import createHistory from 'history/createBrowserHistory';

const history = createHistory();

function Filters() {

  return (
      <div className={styles.options__filters}>
        <Title>
          <h2 className={styles.options__name}>Уровни</h2>
          <h2 className={styles.options__display}>К сожалению, этот раздел доступен только с компьютера</h2>
        </Title>
        <Router history={history}>
          <NavLink
            to='/games/all'
            className={styles.options__filter}
            activeClassName={styles.options__filter_active}
            >
            Все</NavLink>
          <NavLink
            to='/games/creative'
            className={styles.options__filter}
            activeClassName={styles.options__filter_active}
            >
            Пользовательские</NavLink>
          <NavLink
            to='/games/narrative'
            className={styles.options__filter}
            activeClassName={styles.options__filter_active}
            >
            Сюжетные</NavLink>
          <Switch>
            <Route path='/games/:filter?'
              render={({match}) => {
                const {filter} = match.params;
                return <Options getFilter={filter} />
              }} />

          </Switch>

        </Router>
      </div>
  )
}

export default Filters;
