import React, { Component } from 'react';

const withInput = (Wrapper) => {
  return class extends Component {
    constructor(props) {
      super(props);


      this.validate = this.validate.bind(this)
    }

    validate = () => {
      if(this.props.value > 2) {
        console.log('Bravo!')
      } else {
        console.log('Fiasco!')
      }
    }





    render() {
      return (
        <Wrapper {...this.props}/>
      )
    }
  }
}
 export default withInput;
