import React, { Component } from 'react';
import styles from './Input.module.scss';

class ErrorStatus extends Component {
  state = {
    hasError: false
  }

  render() {

    return (
    <form onSubmit={this.testError}>{this.props.children}</form>
    )
  }
}

export {ErrorStatus};

export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newUser: {
        email: '',
        username: '',
        errorStatus: ''
      }
    };

    this.handleChange = this.handleChange.bind(this);
    this.emailFilled = this.emailFilled.bind(this);
    this.testError = this.testError.bind(this);
  }


  handleChange(event) {
    this.setState({value: event.target.value});
  }

  emailFilled(e) {
    let value = e.target.value;
    this.setState( prevState => ({ newUser : 
        {...prevState.newUser, email: value
        }
      }))
  }


  testError() {
    let fullField = this.state.newUser.email;
    let errorStatus = '';
    if (fullField.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      errorStatus = 'valid'
    } else {
      errorStatus = 'invalid'
    }
    console.log(errorStatus)
  }


  render() {
    const {format} = this.props;
    let content = null;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        value={this.state.username}
                        onChange={this.handleChange}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
        <ErrorStatus>
          <button onClick={this.testError} type='button'>test</button>
          <input type='text' 
          testError={this.testError}
          className={styles.feedback__input}
          value={this.state.email}
          onChange={this.emailFilled}
          {...this.props}></input>
        </ErrorStatus>
    }

    return(
      content
    ) 
  }
}
