import React, { Component } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      noError: true
    };

    this.testError = this.testError.bind(this)
  }

  testError = () => {
    const noError = this.state.noError;

    if (this.props.value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      this.props.validate(noError)
    } else {
      this.props.validate()
    }    
  }

  render() {

    const {format} = this.props;
    let content = null;


    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
          <input type='text'
          className={styles.feedback__input}
          name='email'
          {...this.props} />
    }

    return(
      content
    ) 
  }
}
