import React, {Component} from 'react';
import Intro from '../components/Intro/Intro';
import Options from '../components/Options/Options';
import news from '../News/news-data';

export default class Games extends Component {
  constructor(props){
    super(props);
    this.state = {
      filter: 'all',
      levels: news.levels
    }
  }

  filterLogic(levels, filter) {
    levels = this.state.levels;

    switch(filter) {
      case 'all': 
        return levels;
      case 'narrative':
        return levels.filter(item => item.type);
      case 'creative':
        return levels.filter(item => !item.type);
      default:
        return levels
    }
  }

  onFilter = (filter) => {
    this.setState({filter})
  }


  render() {

    const {levels, filter} = this.state;

    const visibleLevels = this.filterLogic(levels, filter)

    return (
      <React.Fragment>
        <Intro label='Играть'/>
        <Options renderLevels={visibleLevels}/>
      </React.Fragment>
    )
  }
}
