import React, { useState } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Title from '../UI/Title/Title';

import styles from './Feedback.module.scss';

import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';

const items = [
  {
    id: 1,
    altText: 'Slide 1',
    caption: 'Slide 1'
  },
  {
    id: 2,
    altText: 'Slide 2',
    caption: 'Slide 2'
  },
  {
    id: 3,
    altText: 'Slide 3',
    caption: 'Slide 3'
  }
];

const newsList = [
  {title: 'Обновление от 25.08.19', text: 'Мы выпустили 100500 новых уровней и искренне поздравляем вас с обновлением! Удачной игры! Удачной игры! Удачной игры!', date: '25/08/2019'},
  {title: 'С 1 сентября!', text: 'Поздравляем наших дорогих подписчиков с этим прекрасным праздником! Поздравляем наших дорогих подписчиков с этим прекрасным праздником! Поздравляем наших дорогих подписчиков с этим прекрасным праздником!', date: '01/11/2019'}
];

const Example = (props) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }

  const slides = items.map((item) => {
    return (
      <CarouselItem
        className="custom-tag"
        tag="div"
        key={item.id}
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}>
          {newsList.map((item) => (
            <Col md={12} lg={6}>
              <div className={styles.news__item}>
                <Title>
                <h3 className={styles.news__title}>{item.title}</h3>
                </Title>
                <p className={styles.news__text}>{item.text}</p>
                <span className={styles.news__date}>{item.date}</span>
              </div>
            </Col>
          ))}
        <CarouselCaption className="text-danger" captionText={item.caption} captionHeader={item.caption} />
      </CarouselItem>
    );
  });

  return (
    <div className={styles.news}>
      <Container>
        <Title>
          <h2 className={styles.news__name}>Новости</h2>
        </Title>
          <Carousel
          activeIndex={activeIndex}
          next={next}
          previous={previous}>
          <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
          {slides}
        </Carousel>
      </Container>
    </div>
  );
}

export default Example;
