import React, { Component, PropTypes } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      error: true
    };

  }

  onBtn = () => {
    // e.preventDefault();

    // const valid = this.state.email;

    // if (this.props.value.length > 2) {
    //   this.props.pushSubmit(valid)
    // } else {
    //   this.props.pushSubmit()
    // }    

    console.log(this.props.value)
  }


  render() {

    const {format} = this.props;
    let content = null;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
      <div>
        <button onClick={this.onBtn} type='button'>show</button>
        <input type='text'
        className={styles.feedback__input}
        name='email'
        {...this.props}></input>
      </div>
    }

    return(
      content
    ) 
  }
}
