import React, { Component } from 'react';
import styles from './Input.module.scss';

export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      username: '',
      errorStatus: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.emailFilled = this.emailFilled.bind(this);
    this.testError = this.testError.bind(this);
  }


  handleChange(event) {
    this.setState({value: event.target.value});
  }

  emailFilled(event) {
    this.setState({value: event.target.value});
  }

  testError() {
    let fullField = this.state.email.value;
    console.log(fullField)

  }


  // emailFilled(e) {
  //   let value = e.target.value;
  //   this.setState( prevState => ({ newUser : 
  //       {...prevState.newUser, email: value
  //       }
  //     }), () => console.log(this.state.newUser))
  // }

  render() {
    const {format} = this.props;
    let content = null;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        value={this.state.username}
                        onChange={this.handleChange}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        value={this.state.email}
                        onChange={this.emailFilled}
                        {...this.props}></input>
    }

    return(
      content
    ) 
  }
}
