import React, { Component } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      isValid: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.emailError = this.emailError.bind(this);
  }

  handleChange = (event) => {
    let name = event.target.name
    let value = event.target.value
    this.setState({[name]: value},
      () => console.log(event.target.value))
  }


  emailError() {
    let fullField = this.state.email;
    let errorStatus = '';
    if (fullField.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      errorStatus = 'valid'
    } else {
      errorStatus = 'invalid'
    }
    console.log(errorStatus)
  }

  // show = () => {
  //   const functor = this.props.testify;
  //   const getData = this.props.value;
  //   if (getData.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
  //     return functor
  //   } else {
  //     console.log('fiasco')
  //   }    
  // }



  render() {

    const {format} = this.props;
    let content = null;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        onChange={this.handleChange}
                        value={this.state.firstrname}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
        <div>
          <button onClick={this.show} type='button'>show</button>
          <input type='text'
          className={styles.feedback__input}
          name='email'
          value={this.state.value}
          onChange={this.handleChange}
          {...this.props}></input>
        </div>
    }

    return(
      content
    ) 
  }
}
