import React from 'react';
import styles from './Filters.module.scss';
import Title from '../Title/Title';
import {NavLink} from 'react-router-dom';

import {Router, Route, Switch} from 'react-router-dom';


const filterButtons = [
  {name: 'all', label: 'Все'},
  {name: 'creative', label: 'Пользовательские'},
  {name: 'narrative', label: 'Сюжетные'},
]

const Filters = ({filter, onFilter}) => {

  const selectorItem = filterButtons.map((item) => {

    return (
      <Route to='/games/:filter?'
        render={({match}) => {
          const {filter} = match.params;
          return <NavLink 
            to={filter === '/games/all' ? '/games/' : `/games/${item.name}`}
            activeClassName={styles.options__filter_active}
            onClick={() => onFilter(item.name)}>
              {item.label}
                </NavLink>
        }}/>
      // <NavLink
      // to={filter === '/games/all' ? '/games/' : `/games/${item.name}`}
      // className={styles.options__filter}
      // activeClassName={styles.options__filter_active}
      // onClick={() => onFilter(item.name)}>
      //   {item.label}
      // </NavLink>
    )})

  return (
      <div className={styles.options__filters}>
        <Title>
          <h2 className={styles.options__name}>Уровни</h2>
          <h2 className={styles.options__display}>К сожалению, этот раздел доступен только с компьютера</h2>
        </Title>

        {selectorItem}
      </div>
  )

}

export default Filters;
