import React, { Component } from 'react';
import styles from './Input.module.scss';


export default withChild = (Wrapper) => {
  return class Input extends Component {
    constructor(props) {
      super(props);
      this.state = {
        email: '',
        firstrname: '',
        lastname: '',
        noError: true
      };
    }

    render() {

      const {format} = this.props;
      let content = null;


      if (format === 'text') {
        content = 
            <input type='text' 
              className={styles.feedback__input}
              {...this.props} />
      } 

      else if (format === 'email') {
        content = 
            <input type='text'
              className={styles.feedback__input}
              name='email'
              {...this.props} />
      }

      return(
        content
      ) 
    }

  }
}
