import React, { Component } from 'react';

const initialState = {
  email: '',
  firstrname: '',
  lastname: '',
  emailError: '',
  firstrnameError: '',
  lastnameError: ''
  // noError: 'true'
}

const withInput = (Wrapper) => {
  return class extends Component {
    constructor(props) {
      super(props);
      this.state = initialState;

      this.inputChange = this.inputChange.bind(this)
      this.pushSubmit = this.pushSubmit.bind(this);
      this.testError = this.testError.bind(this);
      // this.sendData = this.sendData.bind(this);

    }

    inputChange = (e) => {
      const name = e.target.name;
      const value = e.target.value;
      this.setState({[name]: value})
    }

    pushSubmit = (e) => {
      e.preventDefault();
      const isValid = this.testError();
      if(isValid) {
        console.log(this.state)
        this.setState(initialState)
      }
    }

    testError = (getData) => {
      // getData = this.state.email;
      // if(getData.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      //   this.sendData()
      // } else {
      //   console.log('Fiasco')
      // }
      let emailError = '';
      let firstrnameError = '';
      let lastnameError = '';

      if(!this.state.firstrname) {
        firstrnameError = 'invalid'
      }

      if(!this.state.email.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
        emailError = 'invalid'
      }

      if (emailError) {
        this.setState({emailError});
        return false;
      }

      return true
    }

    // sendData = () => {
    //   console.log(this.state)
    // }


    render() {

      return (
        <Wrapper 
              {...this.props}
              inputChange={this.inputChange}
              pushSubmit={this.pushSubmit}
              value={this.state.value} />
      )
    }
  }
}

export default withInput;
