import React, {Component} from 'react';
import Intro from '../components/Intro/Intro';
import Options from '../components/Options/Options';
import news from '../components/News/news-data';
import Filters from '../components/UI/Filters/Filters';
import { Container } from 'reactstrap';

export default class Games extends Component {
  constructor(props){
    super(props);
    this.state = {
      levels: news.levels
    }
  }

  filterLogic(levels, filter) {
    levels = this.state.levels;
    filter = this.props.match.params;

    switch(filter) {
      case (filter === 'all'): 
        return levels;
      case (filter === 'narrative'):
        return levels.filter(item => item.type === filter);
      case (filter === 'creative'):
        return levels.filter(item => item.type === filter);
      default:
        return levels
    }
  }

  render() {

    const {levels} = this.state;
    const {filter} = this.props.match.params;
    const visibleLevels = this.filterLogic(levels, filter)

    return (
      <React.Fragment>
        <Intro introType={'local'} label='Играть'/>
        <Container>
          <Filters />

          <Options renderLevels={visibleLevels}/>
        </Container>
      </React.Fragment>
    )
  }
}
