import React, { Component } from 'react';

const withInput = (Wrapper) => {
  return class extends Component {
    constructor(props) {
      super(props);
      this.state = {
        email: '',
        firstrname: '',
        lastname: '',
        formErrors: {email: '', firstrname: '', lastname: ''},
        noError: 'true'
      }

      this.inputChange = this.inputChange.bind(this)
      this.pushSubmit = this.pushSubmit.bind(this);
      this.testError = this.testError.bind(this);
      this.sendData = this.sendData.bind(this);

    }

    inputChange = (e) => {
      const name = e.target.name;
      const value = e.target.value;
      this.setState({[name]: value})
    }

    // pushSubmit = (e) => {
    //   e.preventDefault();
    //   this.testError()
    // }

    // testError = (getData) => {
    //   getData = this.state.email;
    //   if(getData.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
    //     this.sendData()
    //   } else {
    //     console.log('Fiasco')
    //   }
    // }

    // sendData = () => {
    //   console.log(this.state)
    // }

    pushSubmit = e => {
      e.preventDefault();
      const { data } = this.state;
      const isValid = Object.keys(data).reduce(
          (sum, item) => sum && this.testError(item, data[item]),
          true
      );
      if (isValid) {
        console.log(data);
      }
  };

  testError = (name, value) => {
    if (!value.trim()) {
      this.setState(
        ({ errors }) => ({
          errors: {
            ...errors,
            [name]: 'поле не должно быть пустым',
          },
        }),
        () => false
      );
    } else {
      return true;
    }
};


    render() {
      console.log(Object.keys(this.state))
      return (
        <Wrapper 
              {...this.props}
              inputChange={this.inputChange}
              pushSubmit={this.pushSubmit}
              value={this.state.value} />
      )
    }
  }
}

export default withInput;
