import React, { Component } from 'react';

const initialState = {
  email: '',
  firstrname: '',
  lastname: '',
  emailError: '',
  firstrnameError: '',
  lastnameError: ''
}

const withInput = (Wrapper) => {  
  return class extends Component {
    constructor(props) {
      super(props);
      this.state = initialState;

      this.inputChange = this.inputChange.bind(this);
      this.pushSubmit = this.pushSubmit.bind(this);
      this.testError = this.testError.bind(this);  
    }

    inputChange = (e) => {
      this.setState({[e.target.name]: e.target.value})
    }

    pushSubmit = (e) => {
      e.preventDefault();
      const isValid = this.testError();
      if(isValid) {
        console.log(this.state)
        this.setState(initialState)
      }
    }

    testError = () => {
      let emailError = '';
      let firstrnameError = '';
      let lastnameError = '';

      if(this.state.firstrname.length < 2) {
        firstrnameError = 'invalid'
        console.log(this.state.firstrnameError)
      }

      if(!this.state.email.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
        emailError = 'invalid'
        console.log(this.state.emailError)
      }

      if (emailError || firstrnameError) {
        this.setState({emailError: emailError, firstrnameError: firstrnameError});
        return false;
      }

      return true
    }


    render() {

      return (
        <Wrapper 
              {...this.props}
              inputChange={this.inputChange}
              pushSubmit={this.pushSubmit}
              value={this.state.value} />
      )
    }
  }
}

export default withInput;
