import React, {Component} from 'react';

import { Container, Row, Col } from 'reactstrap';
import Title from '../UI/Title/Title';
import styles from './Options.module.scss';
import Filters from '../UI/Filters/Filters';
import Level from '../Level/Level';
import news from '../News/news-data';

export default class Options extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filter: 'all',
      levels: news.levels
    }
  }

  filterLogic(levels, filter) {
    levels = this.state.levels;

    switch(filter) {
      case 'all': 
        return levels;
      case 'narrative':
        return levels.filter(item => item.type);
      case 'creative':
        return levels.filter(item => !item.type);
      default:
        return levels
    }
  }

  onFilter = (filter) => {
    this.setState({filter})
  }

  render() {
    const {levels} = this.state;

    return (
      <div className={styles.options}>
        <Container>
          <Title subclass={styles.options__title}>
            <h2 className={styles.options__name}>Уровни</h2>
          </Title>
          <Filters onFilter={this.changeFilter}/>
          <ul className={styles.levels__list}>
            <Row>
              {levels.map((item) => (
                <Col lg={4}>
                  <Level description={item.description}
                    number={item.id}>
                    {item.id}
                  </Level>
                </Col>
                  ))}

            </Row>
          </ul>

        </Container>
      </div>
    )
  }
}
