import React, {Component} from 'react';
import { Row, Col } from 'reactstrap';
import styles from './Options.module.scss';
import Level from '../Level/Level';
import Button from '../UI/Button/Button';
import news from '../components/News/news-data';


export default class Options extends Component {
  constructor(props){
    super(props);
    this.state = {
      levels: news.levels
    }
  }

  filterLogic(levels, filter) {
    levels = this.state.levels;
    filter = this.props.match.params;

    switch(filter) {
      case (filter === 'all'): 
        return levels;
      case (filter === 'narrative'):
        return levels.filter(item => item.type === filter);
      case (filter === 'creative'):
        return levels.filter(item => item.type === filter);
      default:
        return levels
    }
  }

  changeFilter = (filter) => {
    this.setState({filter})
  }

  render () {

    const {levels, filter} = this.state;

    const visibleLevels = this.filterLogic(levels, filter)


    return (
      <div className={styles.options}>
        <ul className={styles.options__list}>
          <Row>
            {visibleLevels.map((item) => (
              <Col md={6} lg={5} xl={4}>
                <li className={styles.options__item}>
                  <Level description={item.description}
                    number={item.id}>
                    {item.id}
                  </Level>
                </li>
              </Col>
                ))}                
          </Row>
        </ul>
        <div className={styles.options__button}>
          <Button domType={'button'}>Еще уровни</Button>
        </div>
      </div>
    )

  }


}
