import React, { Component } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      isValid: false
    };

    // this.handleChange = this.handleChange.bind(this);
    // this.testError = this.testError.bind(this);
  }

  // handleChange(e) {
  //   this.props.onChange(e.target.value)
  // }



  show = () => {
    console.log(this.props.value)
  }

  render() {

    const {format} = this.props;
    let content = null;
    const {getData} = this.props.value;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      if(getData.length > 3) {
        content = 
        <div>
          <button onClick={this.show} type='button'>show</button>
          <input type='text'
          className={styles.feedback__input}
          name='email'
          {...this.props}></input>
        </div>
      } else {
        return (
          <div>
          <input type='text' placeholder='ERROR'></input>
        </div>
        )

      }
    }

    return(
      content
    ) 
  }
}
