import React, { Component } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      isValid: false
    };

    this.emailFilled = this.emailFilled.bind(this);
    this.emailError = this.emailError.bind(this);
  }


  emailFilled = (e) => {
    const value = e.target.value;
    this.setState({email: value},
                  () => console.log(this.state));
  }

  emailError() {
    let fullField = this.state.email;
    let errorStatus = '';
    if (fullField.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      errorStatus = 'valid'
    } else {
      errorStatus = 'invalid'
    }
    console.log(errorStatus)
  }

  show = () => {
    // const functor = this.props.onChange;
    // const getData = this.props.value;
    // if (getData.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
    //   return functor
    // } else {
    //   console.log('fiasco')
    // }
    this.setState((state) => {
      return {
        isValid: !state.isValid
      }
    })
    console.log(this.state.isValid)
  }



  render() {

    const {format} = this.props;
    let content = null;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        onChange={this.props.handleChange}
                        value={this.state.firstrname}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
        <div>
          <button onClick={this.show} type='button'>show</button>
          <input type='text'
          className={styles.feedback__input}
          name='email'
          onChange={this.props.handleChange}
          {...this.props}></input>
        </div>
    }

    return(
      content
    ) 
  }
}
