import React, {Component} from 'react';
import { Container, Row, Col } from 'reactstrap';
import Title from '../UI/Title/Title';
import Input from '../UI/Input/Input';
import styles from './Feedback.module.scss';


export default class Feedback extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      firstrname: '',
      lastname: '',
      func: function(validStatus) {
        // if (validStatus) {
        //   this.onSubmit()
        // } 
        // else {
        //   console.log('fiasko!')
        // }
        console.log(validStatus)
      }
    }

    this.inputChange = this.inputChange.bind(this);
    this.testStatus = this.testStatus.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.defaultCancel = this.defaultCancel.bind(this);

  }

  inputChange = (e) => {
    const value = e.target.value;
    this.setState({email: value})
  }


  defaultCancel = (e) => {
    e.preventDefault();
    this.state.func()
  }


  // testStatus = (validStatus) => {
  //   if (validStatus) {
  //     this.onSubmit()
  //   } 
  //   else {
  //     console.log('fiasko!')
  //   }
  // }

  onSubmit() {
    console.log(this.state);
    this.setState({email: ''})
  }


  render() {

    return (
      <div className={styles.feedback}>
        <Container>
          <Title>
            <h2 className={styles.feedback__name}>Связаться</h2>
          </Title>
          <form id='form' onSubmit={this.defaultCancel} action='get'>
            <Row>
              <Col sm={12} md={6}>
                <Input format={'text'} name='firstrname' placeholder='Имя'/>
              </Col>
              <Col sm={12} md={6}>
                <Input format={'text'} name='lastname' placeholder='Фамилия'/>
              </Col>
              <Col sm={12} md={6}>
                <Input morefunc={this.state.func} validate={this.testStatus} onChange={this.inputChange} value={this.state.email} format={'email'} placeholder='Электронная почта'/>
              </Col>
              <Col sm={12} md={6}>
                <input format='text' className={`${styles.feedback__input} ${styles.feedback__input_agenda}`} placeholder='Тема сообщения'></input>
              </Col>
              <Col lg={12}>
                <textarea name='feedback-text' id='feedback' cols='30' rows='5' className={styles.feedback__textarea} placeholder='Текст сообщения'></textarea>
              </Col>
              <Col lg={12}>
                <input className={styles.feedback__button} type='submit' value='Отправить'></input>
              </Col>
            </Row>
          </form>
        </Container>
      </div>
    )

  }
}
