import React, { Component } from 'react';
import styles from './Input.module.scss';


export default class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
        email: '',
        username: ''
    };

    this.testFunc = this.testFunc.bind(this);
  }


  // emailStatus() {
  //   let fullField = this.state.email;
  //   let errorStatus = '';
  //   if (fullField.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
  //     errorStatus = 'valid'
  //   } else {
  //     errorStatus = 'invalid'
  //   }
  //   console.log(errorStatus)
  // }

  testFunc = () => {
    const functor = this.props.testing;
    const getData = this.props.email;

    if (getData.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      return functor
    } else {
      console.log('ERROR')
    }
  }



  render() {
    const {format} = this.props;
    let content = null;

    if (format === 'text') {
      content = <input type='text' 
                        className={styles.feedback__input}
                        onChange={this.props.handleChange}
                        value={this.props.firstrname}
                        {...this.props}></input>
    } 

    else if (format === 'email') {
      content = 
        <div>
          <input type='text'
          className={styles.feedback__input}
          value={this.props.email}
          name='email'
          testing={this.props.handleChange}
          onChange={this.testFunc}
          {...this.props}></input>
        </div>
    }

    return(
      content
    ) 
  }
}
