import React, {Component} from 'react';
import { Row, Col } from 'reactstrap';
import styles from './Options.module.scss';
import Level from '../Level/Level';
import Button from '../UI/Button/Button';
// import news from '../News/news-data';

const levels =  [
  {id: 1, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 2, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 3, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 4, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 5, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 6, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 7, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 8, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 9, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 10, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 11, type: 'creative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'},
  {id: 12, type: 'narrative', description: 'Вы оказались в загадочном лабиринте! Что делать? А вот хз!'}
]


export default class Options extends Component {
  constructor(props) {
    super(props);
    this.state = {}
  }



  render() {

    const visibleLevels = levels.filter(item => {
      return(item.type === this.props.match.params.filter ? !item.type === 'all' : item)
    })

    const selectedLevels = visibleLevels.map((item) => {
      return (
        <Col md={6} lg={5} xl={4}>
          <li className={styles.options__item}>
            <Level description={item.description}
              number={item.id}>
              {item.id}
            </Level>
          </li>
        </Col>
      )
    })

    const shownLevels = selectedLevels.slice(0, 3);

    const hiddenLevels = selectedLevels.slice(3);

    return (
      <div className={styles.options}>
        <ul className={styles.options__list}>
          <Row>
            {shownLevels}
            {/* {initStatus && hiddenLevels} */}
            {/* {visibleLevels.map((item) => (
              <Col md={6} lg={5} xl={4}>
                <li className={styles.options__item}>
                  <Level description={item.description}
                    number={item.id}>
                    {item.id}
                  </Level>
                </li>
              </Col>
                ))}                 */}
          </Row>
        </ul>
        <div className={styles.options__button}>
          <Button domType={'button'}>Еще уровни</Button>
        </div>
      </div>
    ) 

  }

}
