import React, {Component} from 'react';
import Intro from '../components/Intro/Intro';
import Options from '../components/Options/Options';
import news from '../components/News/news-data';
// import Filters from '../components/UI/Filters/Filters';
import { Container } from 'reactstrap';

import {Router, Route, Switch} from 'react-router-dom';
import {NavLink} from 'react-router-dom';
import createHistory from 'history/createBrowserHistory';
const history = createHistory();

export default class Games extends Component {
  constructor(props){
    super(props);
    this.state = {
      filter: 'all',
      levels: news.levels
    }
  }

  filterLogic(levels, filter) {
    levels = this.state.levels;

    switch(filter) {
      case 'all': 
        return levels;
      case 'narrative':
        return levels.filter(item => item.type);
      case 'creative':
        return levels.filter(item => !item.type);
      default:
        return levels
    }
  }

  changeFilter = (filter) => {
    this.setState({filter})
  }

  render() {

    const {levels, filter} = this.state;

    const visibleLevels = this.filterLogic(levels, filter)

    return (
      <React.Fragment>
        <Intro introType={'local'} label='Играть'/>
        <Container>
          {/* <Filters onFilter={this.changeFilter}/> */}
          <Router history={history}>
            <NavLink
              to='/games/all'
              // className={styles.options__filter}
              // activeClassName={styles.options__filter_active}
              onClick={this.filterLogic(levels, filter)}
              >
              Все</NavLink>
            <NavLink
              to='/games/creative'
              // className={styles.options__filter}
              // activeClassName={styles.options__filter_active}
              onClick={this.changeFilter}
              >
              Пользовательские</NavLink>
            <NavLink
              to='/games/narrative'
              // className={styles.options__filter}
              // activeClassName={styles.options__filter_active}
              onClick={this.changeFilter}
              >
              Сюжетные</NavLink>
          </Router>

          <Options renderLevels={visibleLevels}/>
        </Container>
      </React.Fragment>
    )
  }
}
