   return(
    <div>
        {Object.keys(formError).map((fieldName, i) => {
          if(formError[fieldName].length > 0){
            return (
              <p key={i}>{fieldName} {formError[fieldName]}</p>
            )        
          } else {
            return '';
          }
        })}
    </div>

   )
}

export default FormError;
