      //   this.sendData()
      // } else {
      //   console.log('Fiasco')
      // }

    }

    // sendData = () => {
    //   console.log(this.state)
    // }


    render() {

      return (
        <Wrapper 
              {...this.props}
              inputChange={this.inputChange}
              pushSubmit={this.pushSubmit}
              value={this.state.value} />
      )
    }
  }
}

export default withInput;
